class responsiveHeader{constructor(e){this.$navigation=document.querySelector(e.navigation),this.$burgerIcon=document.querySelector(e.burgerIcon),this.overlay="overlay",this.$htmlBody=document.querySelector("body"),this.BurgerClick(),this.WindowClick()}openState(){this.$navigation.classList.add("active"),this.$burgerIcon.classList.add("active"),this.$htmlBody.classList.add(this.overlay)}closeState(){this.$navigation.classList.remove("active"),this.$burgerIcon.classList.remove("active"),this.$htmlBody.classList.remove(this.overlay)}BurgerClick(){this.$burgerIcon.addEventListener("click",()=>{event.currentTarget.classList.contains("active")?this.closeState():this.openState()})}WindowClick(){document.addEventListener("click",e=>{e.target.classList.contains(this.overlay)&&this.closeState()})}}const headerBurgerMenu=new responsiveHeader({navigation:".navigation",burgerIcon:".burgerIcon"});